import{_ as s,o as a,c as l,R as e}from"./chunks/framework.9eb963cb.js";const y=JSON.parse('{"title":"Windows CLI","description":"","frontmatter":{},"headers":[],"relativePath":"cli/windows/windows-cli.md","filePath":"cli/windows/windows-cli.md","lastUpdated":1683798302000}'),n={name:"cli/windows/windows-cli.md"},o=e(`<h1 id="windows-cli" tabindex="-1"><a href="https://learn.microsoft.com/zh-cn/windows-server/administration/windows-commands/windows-commands" target="_blank" rel="noreferrer">Windows CLI</a> <a class="header-anchor" href="#windows-cli" aria-label="Permalink to &quot;[Windows CLI](https://learn.microsoft.com/zh-cn/windows-server/administration/windows-commands/windows-commands)&quot;">​</a></h1><p>这些命令在前端开发中常用于文件和目录的操作、进程管理、网络测试以及命令行窗口的控制等。使用这些命令可以提高工作效率并进行必要的调试和测试。</p><blockquote><p>在使用这些命令时，可以通过在命令后添加 <code>/?</code> 参数来获取更详细的帮助信息，例如 <code>dir /?</code>。</p></blockquote><h2 id="文件和目录操作" tabindex="-1">文件和目录操作 <a class="header-anchor" href="#文件和目录操作" aria-label="Permalink to &quot;文件和目录操作&quot;">​</a></h2><ul><li><code>cd</code> - 切换当前目录。</li><li><code>dir</code> - 列出当前目录中的文件和文件夹。</li><li><code>mkdir</code> - 创建新的文件夹。</li><li><code>rmdir</code> - 删除文件夹。</li><li><code>copy</code> - 复制文件或文件夹。</li><li><code>xcopy</code> - 复制文件和文件夹，并支持递归复制。</li><li><code>del</code> - 删除文件。</li><li><code>ren</code> - 重命名文件或文件夹。</li><li><code>type</code> - 显示文本文件的内容。</li><li><code>tree</code> - 显示目录结构。</li></ul><h2 id="进程管理" tabindex="-1">进程管理 <a class="header-anchor" href="#进程管理" aria-label="Permalink to &quot;进程管理&quot;">​</a></h2><ul><li><code>tasklist</code> - 显示当前运行的进程列表。</li><li><code>taskkill</code> - 终止运行的进程。</li></ul><h2 id="网络相关" tabindex="-1">网络相关 <a class="header-anchor" href="#网络相关" aria-label="Permalink to &quot;网络相关&quot;">​</a></h2><ul><li><code>ping</code> - 测试与另一台计算机的网络连接。</li><li><code>ipconfig</code> - 显示计算机的网络配置信息。</li><li><code>netstat</code> - 显示网络连接和端口信息。</li></ul><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h2><ul><li><code>cls</code> - 清除命令行窗口内容。</li><li><code>exit</code> - 退出命令行窗口。</li><li><code>start</code> - 启动应用程序或打开文件。</li></ul><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><h3 id="端口占用" tabindex="-1">端口占用 <a class="header-anchor" href="#端口占用" aria-label="Permalink to &quot;端口占用&quot;">​</a></h3><ul><li>查看所有端口的占用情况</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">netstat</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-ano</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>查看指定端口的占用情况</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">netstat</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-ano</span><span style="color:#89DDFF;">|</span><span style="color:#FFCB6B;">findstr</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">端口</span><span style="color:#89DDFF;">&quot;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>查看 <code>PID</code> 对应的进程名</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">tasklist</span><span style="color:#89DDFF;">|</span><span style="color:#FFCB6B;">findstr</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">PID</span><span style="color:#89DDFF;">&quot;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ul><li>结束进程</li></ul><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">taskkill</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/f</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/t</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/im</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">PID</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="删除文件-夹" tabindex="-1">删除文件（夹） <a class="header-anchor" href="#删除文件-夹" aria-label="Permalink to &quot;删除文件（夹）&quot;">​</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">#删除文件夹</span></span>
<span class="line"><span style="color:#FFCB6B;">rmdir</span><span style="color:#A6ACCD;"> [/s] </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">/q</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">drive:</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">path</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">#or</span></span>
<span class="line"><span style="color:#FFCB6B;">rd</span><span style="color:#A6ACCD;"> [/s] </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">/q</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">drive:</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">path</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">#只删除文件</span></span>
<span class="line"><span style="color:#FFCB6B;">del</span><span style="color:#A6ACCD;"> [/s] </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">/q</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">drive:</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">path</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><ul><li><p><code>/s</code> 除目录本身外，还将删除指定目录下的所有子目录和文件。用于删除目录树。</p></li><li><p><code>/q</code> 安静模式，带 <code>/s</code> 删除目录树时不要求确认</p></li></ul>`,24),p=[o];function i(c,t,r,d,u,h){return a(),l("div",null,p)}const D=s(n,[["render",i]]);export{y as __pageData,D as default};
